import React, { useState, useEffect } from "react";
import Block from "./Block";

const BlockContainer = ({ dateInfo }) => {
    const [blocks, setBlocks] = useState([]);

    const monthDayConversions = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

    useEffect(() => {
        buildDateBlocks();
    }, []);

    const buildDateBlocks = async () => {
        //sets date block state (for calendar display)
        let newDateBlocks = [];
        for (let i = 1; i <= monthDayConversions[dateInfo.month]; i++) {
            const fullDate = `${i}/${dateInfo.month+1}/${dateInfo.year}`;
            let activities = await fetchActivities(fullDate);
            newDateBlocks.push({
            id: i,
            date: fullDate,
            activities,
            isToday: i === day
            });
        }
        setBlocks([...blocks, ...newDateBlocks]);
    }

    const fetchActivities = async date => {
        let response = await fetch("http://localhost:8000/getactivities", {
          method: "POST",
          mode: "cors",
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ date })
        });
        let data = await response.json();
        return data;
    }

    return (
        <div className="block-container">
            { blocks.map(block =>
                <Block key={block.id} date={block.date} activities={block.activities} isToday={block.isToday}/>
            ) }
        </div>
    );
}

export default BlockContainer;
