import "./style.css";
import expandIcon from "./assets/expand.svg";


const ExpandableList = ({ name, listItems }) =>  {
    let isExpanded = "false"; // this is a string so it can be used as a class
    let expansionClasses = `expansion ${isExpanded}`;

    const toggleExpansion = () => {
        isExpanded == "false" ? isExpanded = "true" : isExpanded = "false";
    }

    return (
        <div className="expandable-list">
            <p>{name}</p>
            <img className="icon" src={expandIcon} onClick={toggleExpansion()}/>
            <div className={expansionClasses}>
                <ul>
                    { listItems.map(listItem => {
                        <li><p>{listItem}</p></li>
                    }) }
                </ul>

            </div>
        </div>
    );
}


export default ExpandableList;
