import { useState, useEffect } from "react";
import BlockContainer from "./BlockContainer";
import Header from "./Header";
import "./style.css";

function App() {
  const [blocks, setBlocks] = useState([]);

  const monthConversions = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

  useEffect(() => {
    //fill date blocks
    const now = new Date();
    const day = now.getDay();
    const month = monthConversions[now.getMonth()];
    const year = now.getFullYear();
    for (let i = 0; i < 30; i++) {
      setBlocks([...blocks, {
        id: 1
      }]);
    }
  }, []);

  return (
    <div className="App">
      <Header />
      <BlockContainer blocks={blocks} />
    </div>
  );
}

export default App;
